sys_relationship.query_with:
(function refineQuery(current, parent) {

	current.addQuery('country_code', parent.country_code);
	current.addQuery('country', parent.country);
	current.addQuery('city', parent.city);
	current.addQuery('latitude', parent.latitude);
	current.addQuery('longitude', parent.longitude);
	current.addQuery('postal_code', parent.postal_code);
	current.addQuery('street', parent.street);
	current.addQuery('number', parent.number);
	current.addQuery('street_number', parent.street_number);
	current.query();

})(current, parent);
